import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchProducts, setFilters, clearFilters } from '../store/slices/productSlice';
import { FaSearch, FaFilter, FaTimes } from 'react-icons/fa';
import ProductCard from '../components/ProductCard';
import './Home.css';

const Home = () => {
  const dispatch = useDispatch();
  const { products, loading, filters } = useSelector((state) => state.products);
  const { isAuthenticated } = useSelector((state) => state.auth);
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    dispatch(fetchProducts(filters));
  }, [dispatch, filters]);

  const handleFilterChange = (key, value) => {
    dispatch(setFilters({ [key]: value }));
  };

  const handleSearch = (e) => {
    e.preventDefault();
    dispatch(fetchProducts(filters));
  };

  const handleClearFilters = () => {
    dispatch(clearFilters());
  };

  const categories = [
    'Smartphones', 'Laptops', 'Tablets', 'Gaming Consoles', 
    'Audio Equipment', 'Cameras', 'Smart Home', 'Other'
  ];

  const conditions = ['New', 'Like New', 'Good', 'Fair', 'Used'];

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero">
        <div className="container">
          <h1>Buy & Sell Used Electronics</h1>
          <p>Find great deals on quality electronics or sell your items to the community</p>
          <div className="hero-buttons">
            {isAuthenticated ? (
              <Link to="/create-product" className="btn btn-primary">
                Start Selling
              </Link>
            ) : (
              <>
                <Link to="/register" className="btn btn-primary">
                  Get Started
                </Link>
                <Link to="/login" className="btn btn-outline">
                  Sign In
                </Link>
              </>
            )}
          </div>
        </div>
      </section>

      <div className="container">
        {/* Filters Section */}
        <section className="filters-section">
          <div className="filters-header">
            <h2>Find What You're Looking For</h2>
            <button 
              className="btn btn-outline filter-toggle"
              onClick={() => setShowFilters(!showFilters)}
            >
              <FaFilter />
              {showFilters ? 'Hide Filters' : 'Show Filters'}
            </button>
          </div>

          {showFilters && (
            <form onSubmit={handleSearch} className="filters-form">
              <div className="filters-grid">
                <div className="filter-group">
                  <label className="filter-label">Search</label>
                  <input
                    type="text"
                    className="filter-input"
                    placeholder="Search products..."
                    value={filters.search}
                    onChange={(e) => handleFilterChange('search', e.target.value)}
                  />
                </div>

                <div className="filter-group">
                  <label className="filter-label">Category</label>
                  <select
                    className="filter-input form-select"
                    value={filters.category}
                    onChange={(e) => handleFilterChange('category', e.target.value)}
                  >
                    <option value="">All Categories</option>
                    {categories.map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>

                <div className="filter-group">
                  <label className="filter-label">Condition</label>
                  <select
                    className="filter-input form-select"
                    value={filters.condition}
                    onChange={(e) => handleFilterChange('condition', e.target.value)}
                  >
                    <option value="">All Conditions</option>
                    {conditions.map(condition => (
                      <option key={condition} value={condition}>{condition}</option>
                    ))}
                  </select>
                </div>

                <div className="filter-group">
                  <label className="filter-label">Min Price</label>
                  <input
                    type="number"
                    className="filter-input"
                    placeholder="Min price"
                    value={filters.minPrice}
                    onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                  />
                </div>

                <div className="filter-group">
                  <label className="filter-label">Max Price</label>
                  <input
                    type="number"
                    className="filter-input"
                    placeholder="Max price"
                    value={filters.maxPrice}
                    onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                  />
                </div>

                <div className="filter-buttons">
                  <button type="submit" className="btn btn-primary">
                    <FaSearch />
                    Search
                  </button>
                  <button 
                    type="button" 
                    className="btn btn-secondary"
                    onClick={handleClearFilters}
                  >
                    <FaTimes />
                    Clear
                  </button>
                </div>
              </div>
            </form>
          )}
        </section>

        {/* Products Section */}
        <section className="products-section">
          <div className="section-header">
            <h2>Available Products</h2>
            {products.length > 0 && (
              <span className="products-count">{products.length} items found</span>
            )}
          </div>

          {loading ? (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              <p>Loading products...</p>
            </div>
          ) : products.length > 0 ? (
            <div className="products-grid">
              {products.map(product => (
                <ProductCard key={product.id} product={product} />
              ))}
            </div>
          ) : (
            <div className="no-products">
              <div className="no-products-content">
                <h3>No products found</h3>
                <p>Try adjusting your filters or check back later for new listings.</p>
                {isAuthenticated && (
                  <Link to="/create-product" className="btn btn-primary">
                    List Your First Item
                  </Link>
                )}
              </div>
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

export default Home;
