import React, { useEffect, useMemo, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchProducts, setFilters, clearFilters } from '../store/slices/productSlice';
import { 
  FaSearch, FaFilter, FaShieldAlt, FaComments, FaCloudUploadAlt,
  FaMobileAlt, FaLaptop, FaHeadphones, FaCamera, FaGamepad, FaHome, FaMicrochip
} from 'react-icons/fa';
import ProductCard from '../components/ProductCard';
import './Home.css';

const CATEGORY_ICONS = {
  Smartphones: FaMobileAlt,
  Laptops: FaLaptop,
  Tablets: FaMicrochip,
  'Gaming Consoles': FaGamepad,
  'Audio Equipment': FaHeadphones,
  Cameras: FaCamera,
  'Smart Home': FaHome,
  Other: FaMicrochip,
};

const Home = () => {
  const dispatch = useDispatch();
  const { products, loading, filters } = useSelector((state) => state.products);
  const { isAuthenticated } = useSelector((state) => state.auth);

  const [heroSearch, setHeroSearch] = useState(filters.search || '');
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    dispatch(fetchProducts(filters));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const featured = useMemo(() => products.slice(0, 8), [products]);

  const categories = [
    'Smartphones', 'Laptops', 'Tablets', 'Gaming Consoles', 
    'Audio Equipment', 'Cameras', 'Smart Home', 'Other'
  ];

  const conditions = ['New', 'Like New', 'Good', 'Fair', 'Used'];

  const runSearch = (e) => {
    e?.preventDefault();
    dispatch(setFilters({ search: heroSearch }));
    dispatch(fetchProducts({ ...filters, search: heroSearch }));
  };

  const selectCategory = (category) => {
    const next = filters.category === category ? '' : category;
    dispatch(setFilters({ category: next }));
    dispatch(fetchProducts({ ...filters, category: next }));
  };

  const handleAdvancedChange = (key, value) => {
    dispatch(setFilters({ [key]: value }));
  };

  const handleClearAll = () => {
    setHeroSearch('');
    dispatch(clearFilters());
    dispatch(fetchProducts({}));
  };

  return (
    <div className="home">
      {/* HERO */}
      <section className="hero-modern">
        <div className="container hero-inner">
          <div className="hero-copy">
            <h1>Find Tech You Love. Sell What You Don't.</h1>
            <p>Curated marketplace for quality used electronics with secure accounts and realâ€‘time chat.</p>

            <form className="search-bar" onSubmit={runSearch}>
              <FaSearch className="search-icon" />
              <input
                type="text"
                value={heroSearch}
                onChange={(e) => setHeroSearch(e.target.value)}
                placeholder="Search phones, laptops, cameras..."
              />
              <button type="submit" className="btn btn-primary">Search</button>
            </form>

            <div className="category-chips">
              {categories.map((cat) => {
                const Icon = CATEGORY_ICONS[cat];
                const active = filters.category === cat;
                return (
                  <button
                    key={cat}
                    className={`chip ${active ? 'active' : ''}`}
                    onClick={() => selectCategory(cat)}
                    type="button"
                  >
                    <Icon className="chip-icon" />
                    <span>{cat}</span>
                  </button>
                );
              })}
            </div>

            <div className="hero-cta">
              {isAuthenticated ? (
                <Link to="/create-product" className="btn btn-primary">List an Item</Link>
              ) : (
                <>
                  <Link to="/register" className="btn btn-primary">Get Started</Link>
                  <Link to="/login" className="btn btn-outline">Sign In</Link>
                </>
              )}
              <button className="btn btn-outline" onClick={() => setShowFilters((s) => !s)}>
                <FaFilter /> {showFilters ? 'Hide Filters' : 'Advanced Filters'}
              </button>
              <button className="btn btn-secondary" onClick={handleClearAll}>Reset</button>
            </div>
          </div>
        </div>
      </section>

      {/* FEATURES */}
      <section className="section features">
        <div className="container">
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon gradient-1"><FaShieldAlt /></div>
              <h3>Safe & Secure</h3>
              <p>Session-based login, encrypted passwords, and rate-limited APIs keep your account protected.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon gradient-2"><FaComments /></div>
              <h3>Direct Chat</h3>
              <p>Message buyers and sellers instantly with our built-in WebSocket chat experience.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon gradient-3"><FaCloudUploadAlt /></div>
              <h3>Easy Listings</h3>
              <p>Create beautiful listings with images, categories, and rich details in just a few clicks.</p>
            </div>
          </div>
        </div>
      </section>

      {/* FEATURED PRODUCTS */}
      <section className="section featured">
        <div className="container">
          <div className="section-header">
            <h2>Featured Listings</h2>
            {products.length > 0 && (
              <span className="products-count">{products.length} items</span>
            )}
          </div>

          {loading ? (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              <p>Loading products...</p>
            </div>
          ) : featured.length > 0 ? (
            <div className="products-grid">
              {featured.map((product) => (
                <ProductCard key={product.id} product={product} />
              ))}
            </div>
          ) : (
            <div className="no-products">
              <div className="no-products-content">
                <h3>No products yet</h3>
                <p>Be the first to list an item or try adjusting your search.</p>
                {isAuthenticated ? (
                  <Link to="/create-product" className="btn btn-primary">List an Item</Link>
                ) : (
                  <Link to="/register" className="btn btn-primary">Create an Account</Link>
                )}
              </div>
            </div>
          )}
        </div>
      </section>

      {/* ADVANCED FILTERS */}
      {showFilters && (
        <section className="filters-section advanced-filters">
          <div className="container">
            <form onSubmit={(e) => { e.preventDefault(); dispatch(fetchProducts(filters)); }} className="filters-form">
              <div className="filters-grid">
                <div className="filter-group">
                  <label className="filter-label">Search</label>
                  <input
                    type="text"
                    className="filter-input"
                    placeholder="Search products..."
                    value={filters.search}
                    onChange={(e) => handleAdvancedChange('search', e.target.value)}
                  />
                </div>

                <div className="filter-group">
                  <label className="filter-label">Category</label>
                  <select
                    className="filter-input form-select"
                    value={filters.category}
                    onChange={(e) => handleAdvancedChange('category', e.target.value)}
                  >
                    <option value="">All Categories</option>
                    {categories.map((category) => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>

                <div className="filter-group">
                  <label className="filter-label">Condition</label>
                  <select
                    className="filter-input form-select"
                    value={filters.condition}
                    onChange={(e) => handleAdvancedChange('condition', e.target.value)}
                  >
                    <option value="">All Conditions</option>
                    {conditions.map((condition) => (
                      <option key={condition} value={condition}>{condition}</option>
                    ))}
                  </select>
                </div>

                <div className="filter-group">
                  <label className="filter-label">Min Price</label>
                  <input
                    type="number"
                    className="filter-input"
                    placeholder="0"
                    value={filters.minPrice}
                    onChange={(e) => handleAdvancedChange('minPrice', e.target.value)}
                  />
                </div>

                <div className="filter-group">
                  <label className="filter-label">Max Price</label>
                  <input
                    type="number"
                    className="filter-input"
                    placeholder="1000"
                    value={filters.maxPrice}
                    onChange={(e) => handleAdvancedChange('maxPrice', e.target.value)}
                  />
                </div>

                <div className="filter-buttons">
                  <button type="submit" className="btn btn-primary">
                    <FaSearch /> Apply
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={handleClearAll}>
                    Reset
                  </button>
                </div>
              </div>
            </form>
          </div>
        </section>
      )}
    </div>
  );
};

export default Home;
