import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchProducts, setFilters, clearFilters } from '../store/slices/productSlice';
import { 
  FaSearch, FaFilter, FaShieldAlt, FaComments, FaRocket,
  FaMobileAlt, FaLaptop, FaHeadphones, FaCamera, FaGamepad, FaHome, FaMicrochip,
  FaArrowRight, FaStar, FaHeart, FaEye, FaBars, FaUser, FaShoppingCart, FaMapMarkerAlt
} from 'react-icons/fa';
import ProductCard from '../components/ProductCard';
import './Home.css';

const Home = () => {
  const dispatch = useDispatch();
  const { products, loading, filters } = useSelector((state) => state.products);
  const { isAuthenticated } = useSelector((state) => state.auth);

  const [searchQuery, setSearchQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Amazon-style hero images
  const heroImages = [
    '/placeholder-image.svg',
    '/placeholder-image.svg', 
    '/placeholder-image.svg',
    '/placeholder-image.svg'
  ];

  useEffect(() => {
    dispatch(fetchProducts(filters));
  }, [dispatch]);

  // Rotate hero images every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [heroImages.length]);

  const categories = [
    { name: 'Smartphones', icon: FaMobileAlt, color: '#FF9900' },
    { name: 'Laptops', icon: FaLaptop, color: '#232F3E' },
    { name: 'Tablets', icon: FaMicrochip, color: '#37475A' },
    { name: 'Gaming', icon: FaGamepad, color: '#FF9900' },
    { name: 'Audio', icon: FaHeadphones, color: '#232F3E' },
    { name: 'Cameras', icon: FaCamera, color: '#37475A' },
    { name: 'Smart Home', icon: FaHome, color: '#FF9900' },
    { name: 'Other', icon: FaMicrochip, color: '#232F3E' }
  ];

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      dispatch(setFilters({ search: searchQuery }));
      dispatch(fetchProducts({ ...filters, search: searchQuery }));
    }
  };

  const selectCategory = (category) => {
    dispatch(setFilters({ category }));
    dispatch(fetchProducts({ ...filters, category }));
  };

  const handleClearFilters = () => {
    setSearchQuery('');
    dispatch(clearFilters());
    dispatch(fetchProducts({}));
  };

  const featuredProducts = products.slice(0, 8);

  return (
    <div className="amazon-home">
      {/* Amazon-style Header */}
      <header className="amazon-header">
        <div className="header-content">
          <div className="header-left">
            <button className="menu-btn">
              <FaBars />
              <span>All</span>
            </button>
            <Link to="/" className="amazon-logo">
              <span className="logo-text">Electronics</span>
              <span className="logo-subtext">Marketplace</span>
            </Link>
          </div>

          <div className="header-search">
            <form onSubmit={handleSearch} className="search-form">
              <select className="search-category">
                <option>All Categories</option>
                {categories.map((cat) => (
                  <option key={cat.name} value={cat.name}>{cat.name}</option>
                ))}
              </select>
              <input
                type="text"
                placeholder="Search for products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="search-input"
              />
              <button type="submit" className="search-btn">
                <FaSearch />
              </button>
            </form>
          </div>

          <div className="header-right">
            <div className="header-nav">
              <Link to="/profile" className="nav-item">
                <FaUser />
                <span>{isAuthenticated ? 'Account' : 'Sign In'}</span>
              </Link>
              <Link to="/orders" className="nav-item">
                <span>Orders</span>
              </Link>
              <Link to="/cart" className="nav-item cart">
                <FaShoppingCart />
                <span>Cart</span>
                <span className="cart-count">0</span>
              </Link>
            </div>
          </div>
        </div>
      </header>

      {/* Amazon-style Navigation */}
      <nav className="amazon-nav">
        <div className="nav-content">
          <div className="nav-left">
            <button className="nav-btn">
              <FaBars />
              <span>All</span>
            </button>
            <Link to="/deals" className="nav-link">Today's Deals</Link>
            <Link to="/customer-service" className="nav-link">Customer Service</Link>
            <Link to="/registry" className="nav-link">Registry</Link>
            <Link to="/gift-cards" className="nav-link">Gift Cards</Link>
            <Link to="/sell" className="nav-link">Sell</Link>
          </div>
          <div className="nav-right">
            <Link to="/location" className="nav-link location">
              <FaMapMarkerAlt />
              <span>Deliver to</span>
            </Link>
          </div>
        </div>
      </nav>

      {/* Hero Carousel */}
      <section className="hero-carousel">
        <div className="carousel-container">
          {heroImages.map((image, index) => (
            <div
              key={index}
              className={`carousel-slide ${index === currentImageIndex ? 'active' : ''}`}
              style={{ backgroundImage: `url(${image})` }}
            />
          ))}
          
          {/* Carousel Navigation */}
          <div className="carousel-nav">
            {heroImages.map((_, index) => (
              <button
                key={index}
                className={`carousel-dot ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => setCurrentImageIndex(index)}
              />
            ))}
          </div>

          {/* Carousel Arrows */}
          <button 
            className="carousel-arrow carousel-prev"
            onClick={() => setCurrentImageIndex((prev) => (prev - 1 + heroImages.length) % heroImages.length)}
          >
            ‹
          </button>
          <button 
            className="carousel-arrow carousel-next"
            onClick={() => setCurrentImageIndex((prev) => (prev + 1) % heroImages.length)}
          >
            ›
          </button>
        </div>
      </section>

      {/* Categories Grid */}
      <section className="categories-section">
        <div className="container">
          <div className="categories-grid">
            {categories.map((category) => (
              <button
                key={category.name}
                className={`category-card ${filters.category === category.name ? 'active' : ''}`}
                onClick={() => selectCategory(category.name)}
                style={{ '--category-color': category.color }}
              >
                <div className="category-icon">
                  <category.icon />
                </div>
                <span className="category-name">{category.name}</span>
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Products */}
      <section className="featured-section">
        <div className="container">
          <div className="section-header">
            <h2>Featured Products</h2>
            <Link to="/products" className="view-all-link">
              View All <FaArrowRight />
            </Link>
          </div>

          {loading ? (
            <div className="loading-state">
              <div className="loading-spinner"></div>
              <p>Loading amazing products...</p>
            </div>
          ) : featuredProducts.length > 0 ? (
            <div className="products-grid">
              {featuredProducts.map((product) => (
                <ProductCard key={product.id} product={product} />
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <div className="empty-icon">📱</div>
              <h3>No products yet</h3>
              <p>Be the first to list an item and start selling!</p>
              {isAuthenticated ? (
                <Link to="/create-product" className="btn btn-primary">
                  List Your First Item
                </Link>
              ) : (
                <Link to="/register" className="btn btn-primary">
                  Join the Community
                </Link>
              )}
            </div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <div className="cta-content">
            <h2>Ready to Start Selling?</h2>
            <p>Join thousands of successful sellers and turn your unused electronics into cash.</p>
            <div className="cta-buttons">
              {isAuthenticated ? (
                <Link to="/create-product" className="btn btn-primary btn-large">
                  List an Item
                </Link>
              ) : (
                <>
                  <Link to="/register" className="btn btn-primary btn-large">
                    Get Started Free
                  </Link>
                  <Link to="/login" className="btn btn-outline btn-large">
                    Sign In
                  </Link>
                </>
              )}
            </div>
          </div>
        </div>
      </section>

      {/* Advanced Filters */}
      {showFilters && (
        <section className="filters-panel">
          <div className="container">
            <div className="filters-header">
              <h3>Advanced Filters</h3>
              <button 
                className="close-filters"
                onClick={() => setShowFilters(false)}
              >
                ×
              </button>
            </div>
            
            <div className="filters-content">
              <div className="filter-row">
                <div className="filter-group">
                  <label>Price Range</label>
                  <div className="price-inputs">
                    <input
                      type="number"
                      placeholder="Min"
                      className="filter-input"
                      value={filters.minPrice || ''}
                      onChange={(e) => dispatch(setFilters({ minPrice: e.target.value }))}
                    />
                    <span>to</span>
                    <input
                      type="number"
                      placeholder="Max"
                      className="filter-input"
                      value={filters.maxPrice || ''}
                      onChange={(e) => dispatch(setFilters({ maxPrice: e.target.value }))}
                    />
                  </div>
                </div>
                
                <div className="filter-group">
                  <label>Condition</label>
                  <select
                    className="filter-input"
                    value={filters.condition || ''}
                    onChange={(e) => dispatch(setFilters({ condition: e.target.value }))}
                  >
                    <option value="">Any Condition</option>
                    <option value="New">New</option>
                    <option value="Like New">Like New</option>
                    <option value="Good">Good</option>
                    <option value="Fair">Fair</option>
                    <option value="Used">Used</option>
                  </select>
                </div>
                
                <div className="filter-actions">
                  <button 
                    className="btn btn-primary"
                    onClick={() => dispatch(fetchProducts(filters))}
                  >
                    Apply Filters
                  </button>
                  <button 
                    className="btn btn-secondary"
                    onClick={handleClearFilters}
                  >
                    Clear All
                  </button>
                </div>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Toggle Filters Button */}
      <button 
        className="filters-toggle"
        onClick={() => setShowFilters(!showFilters)}
      >
        <FaFilter />
        {showFilters ? 'Hide Filters' : 'Show Filters'}
      </button>
    </div>
  );
};

export default Home;
